def file_creator(filename, content):
    try:
        with open(filename + ".html", "w") as f:
            f.write(content)
    except:
        print(":::ERROR:::")


def cssNormalizer(filename):
    process_1 = re.sub(";", ";\n", filename)
    process_2 = re.sub("{", "{\n", process_1)
    process_3 = re.sub("}", "}\n\n", process_2)
    return process_3


